CREATE TABLE `resenha` (
  `id_resenha` int(11) NOT NULL AUTO_INCREMENT,
  `id_livro` int(11) NOT NULL,
  `id_escritor` int(11) NOT NULL,
  `id_autor_resenha` int(11) NOT NULL,
  `titulo` varchar(80) NOT NULL,
  `slug` varchar(255) NOT NULL,
  `path` varchar(255) NOT NULL,
  `genero` varchar(20) NOT NULL,
  `sinopse` text NOT NULL,
  `conteudo` text NOT NULL,
  `data_publicacao` timestamp NOT NULL DEFAULT current_timestamp(),
  `data_atualizacao` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `visualizacao` int(11) DEFAULT 0,
  PRIMARY KEY (`id_resenha`),
  UNIQUE KEY `slug` (`slug`),
  KEY `id_livro` (`id_livro`),
  KEY `id_escritor` (`id_escritor`),
  KEY `id_autor_resenha` (`id_autor_resenha`),
  CONSTRAINT `resenha_ibfk_1` FOREIGN KEY (`id_livro`) REFERENCES `livro` (`id_livro`),
  CONSTRAINT `resenha_ibfk_2` FOREIGN KEY (`id_escritor`) REFERENCES `escritor` (`id_escritor`),
  CONSTRAINT `resenha_ibfk_3` FOREIGN KEY (`id_autor_resenha`) REFERENCES `autor_resenha` (`id_autor_resenha`)
) ENGINE=InnoDB AUTO_INCREMENT=28 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;
